//  });

  useEffect(()=>{
    
    const fetchUser = ()=> {
      const userRef = projectFirestore.collection("users").doc(uid)
      userRef.get()
      .then((doc)=>{
        setUserData(doc.data())
      })

    }
    console.log(userData)
    return ()=>{ 
      fetchUser()
    }
   
  },[])
   


