import {useState} from 'react'
import { projectAuth,projectFirestore } from '../firebase/config'

 const UpdateProfile = ({userid}) => {
   const[location,setLocation]=useState("")
   const[bio,setBio]=useState("")
  
   const handleSubmit = async (e)=>{
    e.preventDefault()
     await  projectFirestore.collection("users").doc(userid).add({
      city: location,
      bio: bio
  }).then(()=>{
    console.log("update success")
  }).catch((error)=>{
    console.error("Error writing document: ", error);
  })
    return
   }
  return (
    <>
      <a className="waves-effect waves-light btn modal-trigger color green accent-4" href="#modal">Update details</a>
        <div id="modal" className="modal">
        <form className="col s12 m10 l8 offset-l2" onSubmit={handleSubmit} > 
          <div className="modal-content">
          </div>
           
            
            <div className="input-field center">
                <button type="submit" className="btn-large color green accent-4" >Submit</button>
              </div>

         </form>      
      </div>
    </>
  )
}

export default UpdateProfile
