import {useEffect,useState} from 'react'
import {projectAuth,projectStorage,projectFirestore} from '../firebase/config'
import {Link} from 'react-router-dom'

const Dashboard =  () => {

   const [authListen, setAuthListen] = useState(false)
   const[userName,setUserName] = useState(null)

   useEffect(()=>{
      const getUser = projectAuth.onAuthStateChanged((user)=>{
         if(user){
            setAuthListen(true)
            
         }else{
            setAuthListen(false)
         }      
   })
   getUser() 
   })

  


   if (authListen){
      return (
         <>
            <h1>{userName}</h1>
              <button class="btn color green accent-4  waves-effect waves-light">
              <Link to="/AddPet" className="white-text">Add a Pet</Link>
            </button>
           <button class="btn color green accent-4 waves-effect waves-light">
              <Link to="/PetGrid" className=" white-text">Add a Pet</Link>
           </button>    
              </>
        );
   }else{
      return(
         <h1>Loading</h1>
      )
      
   }
     

}
 
export default Dashboard;
