import {useEffect,useState} from 'react'
import M from 'materialize-css'
import {projectAuth,projectStorage,projectFirestore} from '../firebase/config'
import Loading from './Loading'
import UpdateProfile from './UpdateProfile'

const Dashboard =  () => {

   const[userName,setUserName] = useState(null)
   const[authUser,setAuthUser] = useState(null)
   const[uid,setUid]=useState()
   const[userData,setUserData]=useState()

   useEffect(()=>{
    var elems = document.querySelectorAll('.modal');
    var instances = M.Modal.init(elems,{});
   })

// useEffect(()=>{
  
// })

   useEffect(()=>{
    const unlisten = projectAuth.onAuthStateChanged(
       authUser => {
         if(authUser)
         { setAuthUser(authUser)
          setUserName(authUser.displayName)
          setUid(authUser.uid)
         }
           else {setAuthUser(null)}
       },       
    )
     return()=>{
      unlisten()
     };
    
 },[userName,]);

  useEffect(()=>{

  // let userDetails = projectAuth.currentUser

  // if(userDetails){
  //   console.log(userDetails)
  //   setAuthUser(userDetails)
  //   setUserName(userDetails.displayName)
  //   setUid(userDetails.uid)
  // }else{
  //   console.log()
  // }
    
    const fetchUser = ()=> {
      const userRef = projectFirestore.collection("users").doc(uid)
      userRef.get()
      .then((doc)=>{
        setUserData(doc.data())
      })

    }
    console.log(userData)

    return ()=>{ 
      fetchUser()
    }
   
  },[uid,authUser])
   


  // // const fetchUsers = async ()=>{
  // //   const usersColection = await projectFirestore.collection("users").doc(uid).get()
  // //   return setDocData(usersColection.data())
  // //   // usersColection.docs.map((doc)=>{
  // //   //   console.log(doc.data())
  // //   // })
  // }
  
 

     if(!authUser) {
      return <Loading/>
     }  return (
       <div className="container">
         <h2 className="center">Hello {userName}</h2>
         <div className="row">
            <div className="col s12 m8 offset-m2 l6 offset-l3">
            {
              userData ? (<h5>City:{userData.city}</h5>)
              : (<h5>City:Update</h5>)
            }
            {
              userData ? (<h5>Bio:{userData.bio}</h5>)
              : (<h5>Bio:Update</h5>)

            }
              
              <a className="waves-effect waves-light btn modal-trigger color green accent-4" href="#modal">Update details</a>
              <div id="modal" className="modal">
                <div className="modal-content">
                  <UpdateProfile userid={uid}/> 
                </div>
              </div>
            </div>
         </div>      
       </div>
        );
     

}
 
export default Dashboard;
