import {useState, useContext} from 'react'
import { firebaseAuth } from '../provider/authProvider'
import { Redirect } from "react-router-dom";

const LogIn = () => {
  const methods = useContext(firebaseAuth)

  const[email,setEmail] = useState("")
  const[password,setPassword] = useState("")

  const handleSubmit = (event)=>{
    event.preventDefault()
    methods.Signup(email,password)
    setCurrentUser(true)
} 

  return ( 
    <>
      <h1 className="center">Log In</h1>
      <div className="row">
        <div className="col s12 m8 l4 offset-l4">
        <form id="log-in" onSubmit={handleSubmit}>
          <div className="input-field">
            <input type="email" id="email" className="validate" onChange={ e => setEmail(e.target.value)}></input>
            <label htmlFor="email">Please enter your email</label>
          </div>
          <div className="input-field">
            <input type="password" id="password" className="validate"></input>
            <label htmlFor="password">Please enter your password</label>
          </div>
          <div className="input-field center">
            <button type="submit" className="btn-large color green accent-4" >Submit</button>
          </div>
        </form>
       </div>
      </div>
    </>
    
   );
}
 
export default LogIn;
