import {useEffect,useState} from 'react'
import {projectAuth,projectStorage,projectFirestore} from '../firebase/config'
import Loading from './Loading'
import {Link,Redirect} from 'react-router-dom'

const Dashboard =  () => {

   const[userName,setUserName] = useState(null)
   const[authUser,setAuthUser] = useState(null)

   useEffect(()=>{

      const unlisten = projectAuth.onAuthStateChanged(
         authUser => {
           if(authUser){setAuthUser(authUser)
                  setUserName(authUser.displayName)
           }
             else {setAuthUser(null)}
         },
      );
      return () => {
          unlisten();
      }
   });

     if(!auth) {
      return <Loading/>
     }  return (
      <> 
        {
           logedIn ? (
            <>
            <h1>{userName}</h1>
            <button class="btn color green accent-4  waves-effect waves-light">
            <Link to="/AddPet" className="white-text">Add a Pet</Link>
          </button>
         <button class="btn color green accent-4 waves-effect waves-light">
            <Link to="/PetGrid" className=" white-text">Add a Pet</Link>
         </button> 
         </>
           ) : (
            <Redirect to="/SignUp" />
           )
        }
              </>
        );
     

}
 
export default Dashboard;
