import {useEffect,useState} from 'react'
import {projectAuth,projectStorage,projectFirestore} from '../firebase/config'
import {Link} from 'react-router-dom'

const Dashboard = () => {

   const[name , setName] = useState("")
   const[description, setDescription] = useState("")
   const[url,setUrl]= useState(null)
   const[pets,setPets]= useState([])

   const handleSubmit = e =>{
      e.preventDefault()
      // console.log(name,description,file.name)
   
      projectFirestore.collection('pets').add({
         name:name,
         description:description,
         avatar:url
      })
      setName("")
      setDescription("")
   }

   const fileHandler =  async (e)=>{
      const file = e.target.files[0]   
      const storageRef = projectStorage.ref()
      const fileRef = storageRef.child(file.name)
      // const collectionRef = projectFirestore.collection('pets')
      await fileRef.put(file)
      setUrl(await fileRef.getDownloadURL())
   }

   useEffect(()=>{
      const fetchPets = async () => {
         const petsCollection = await projectFirestore.collection("pets").get();
         setPets(
           petsCollection.docs.map((doc) => {
             return doc.data();
           })
         );
       };
       fetchPets();
   },[handleSubmit])

  return ( 
   
   // <>
   //    <button class="btn waves-effect waves-light">
   //       <Link to="/AddPet">Add a Pet</Link>
   //    </button>
   //    <button class="btn waves-effect waves-light">
   //       <Link to="/PetGrid">Add a Pet</Link>
   //    </button>
   // </>
   <div className="container">
      <div className="row">
         <form id="add-pet" className="col s10 m5 l4 offset-s1 offset-l2" onSubmit={handleSubmit}>
         <h4 className ="center">Add your pet</h4>
            <div className="input-field">
               <input type="text" id="pet-name" className="validate" value={name} onChange={e => setName(e.target.value)}></input>
               <label htmlFor="pet-name">Add pet name</label>
            </div>
            <div className="input-field">
               <textarea className="materialize-textarea validate" id="pet-description" value={description} onChange={e => setDescription(e.target.value)}></textarea>
               <label htmlFor="pet-description">Add a description</label>
            </div>
            <div className="file-field input-field">
                <div className="btn color green accent-4">
                  <span>Add</span>
                  <input type="file" onChange={fileHandler} />
               </div>
               <div className="file-path-wrapper">
                     <input className="file-path"/>
                  </div>
            </div>
            <div className="input-field center">
              <button type="submit" className="btn color green accent-4" >Submit</button>
            </div>
         </form>
         <div className="col s10 m5 l4 offset-l1">
      <ul >
      {pets.map((pet) => {
          return (
          <li key={pet.name}>
               <div class="card">
                  <div className="card-image waves-effect waves-block waves-light">
                     <img className="activator" src={pet.avatar}/>
                  </div>
                  <div className="card-content">
                     <span className="card-title activator grey-text text-darken-4">{pet.name}<i class="material-icons right">more_vert</i></span>
                     <p><a href="#">This is a link</a></p>
                  </div>
                  <div className="card-reveal">
                     <span className="card-title grey-text text-darken-4">Card Title<i className="material-icons right">close</i></span>
                     <p>{pet.description}</p>
                  </div>
               </div>
           </li> 
          );
        })}
      </ul>
         </div>
      </div>
   </div>
   );
}
 
export default Dashboard;
