import {useEffect,useState} from 'react'
import {projectAuth,projectStorage,projectFirestore} from '../firebase/config'
import {Link} from 'react-router-dom'

const Dashboard =  () => {


   const[userName,setUserName] = useState(null)

   const getUser = projectAuth.onAuthStateChanged((user)=>{
         if(user){
            console.log(user.displayName)
            setUserName(user.displayName)
           return user
         }else{
            console.log("user is not ready")
         }      
  
      getUser() 

   })

  return ( 
   
   {
      if (userName){
         return (<>
            <h1>{userName}</h1>
         <button class="btn color green accent-4  waves-effect waves-light">
         <Link to="/AddPet" className="white-text">Add a Pet</Link>
      </button>
      <button class="btn color green accent-4 waves-effect waves-light">
         <Link to="/PetGrid" className=" white-text">Add a Pet</Link>
      </button>

         </>)
      }

   }
      
      

   );
}
 
export default Dashboard;
